CREATE TABLE click (id BIGINT AUTO_INCREMENT, site_id BIGINT NOT NULL, referer VARCHAR(100), uri VARCHAR(200), height SMALLINT, width SMALLINT, avail_height SMALLINT, avail_width SMALLINT, inner_height SMALLINT, inner_width SMALLINT, x SMALLINT, y SMALLINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX site_id_idx (site_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE site (id BIGINT AUTO_INCREMENT, name VARCHAR(100), url VARCHAR(100), code VARCHAR(100), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE click ADD CONSTRAINT click_site_id_site_id FOREIGN KEY (site_id) REFERENCES site(id) ON DELETE CASCADE;
